    try:
        data = request.get_json()
        username = data.get('username', '').strip()
        password = data.get('password', '').strip()

        # Get client IP for rate limiting
        client_ip = request.environ.get('HTTP_X_FORWARDED_FOR', request.environ.get('REMOTE_ADDR', 'unknown'))

        # Authenticate user
        result = security_system.authenticate_user(username, password, client_ip)

        if result['success']:
            # Store user in session (Ollama WebUI style - no JWT tokens needed)
            session['user'] = result['user']
            print(f"User stored in session: {result['user']['username']}")  # Debug

            return jsonify(result)
        else:
            return jsonify(result), 401

    except Exception as e:
        print(f"Login error: {e}")
        return jsonify({'error': 'Authentication service error'}), 500

@app.route('/auth/register', methods=['GET', 'POST'])
def register_page():
    """Handle user registration (Ollama WebUI style)"""
    if request.method == 'GET':
        return render_template('register.html')

    # POST request - create user
    try:
        data = request.get_json()
        username = data.get('username', '').strip()
        password = data.get('password', '').strip()
        confirm_password = data.get('confirm_password', '').strip()

        # Validation
        if not username or not password:
            return jsonify({'error': 'Username and password required'}), 400

        if password != confirm_password:
            return jsonify({'error': 'Passwords do not match'}), 400

        if len(username) < 3 or len(username) > 20:
            return jsonify({'error': 'Username must be 3-20 characters'}), 400

        if len(password) < 6:
            return jsonify({'error': 'Password must be at least 6 characters'}), 400

        # Create user
        result = security_system.create_user(username, password, 'user')

        if result['success']:
            return jsonify({
                'success': True, 
                'message': 'User created successfully! Please login.'
            })
        else:
            return jsonify(result), 400

    except Exception as e:
        print(f"Registration error: {e}")
        return jsonify({'error': 'Registration service error'}), 500

@app.route('/auth/logout', methods=['POST'])
def auth_logout():
    """Handle logout"""
    session.clear()
    return jsonify({'success': True, 'message': 'Logged out successfully'})

@app.route('/auth/status')
def auth_status():
    """Check authentication status"""
    token = session.get('auth_token')

    if not token or not security_system:
        return jsonify({'authenticated': False})

    payload = security_system.verify_session_token(token)
    return jsonify({
        'authenticated': payload is not None,
        'user': payload if payload else None
    })

@app.before_request
def check_auth():
    """Check authentication for protected routes (Ollama WebUI style)"""
    # Public routes that don't require auth
    public_routes = ['/login', '/auth/login', '/auth/status', '/auth/register', '/static']

    path = request.path
    for route in public_routes:
        if path == route or path.startswith(route + '/'):
            return  # Allow access

    # Check for security system
    if not security_system:
        return render_template('login.html'), 401

    # Check if user is logged in (session-based like Ollama WebUI)
    if 'user' not in session or not session['user']:
        return redirect('/login')

    # Store user info in request
    request.current_user = session['user']

