#!/usr/bin/env python3
"""
SECURE Security Module for Sara AI - VERSION 2.0
NO BACKDOORS - PROPER AUTHENTICATION ONLY
Provides secure login, session management, and user management
"""

import hashlib
import secrets
import jwt
import json
import os
from datetime import datetime, timedelta
from functools import wraps

class SaraSecuritySystem:
    def __init__(self, users_file=None):
        self.secret_key = secrets.token_urlsafe(64)  # Much stronger
        self.session_timeout = 3600  # 1 hour
        self.failed_attempts = {}
        self.max_attempts = 3  # Stricter
        self.lockout_duration = 1800  # 30 minutes

        # Users file location
        self.users_file = users_file or os.path.join(os.path.dirname(__file__), 'sara_users.json')

        # Initialize users with NO DEFAULT ADMIN
        self.users = self._load_users()

        # Create admin only if no users exist (first setup)
        if not self.users:
            print("‚ö†Ô∏è  FIRST SETUP: Please create admin account via registration")
            print("   No default credentials provided for security")

    def _load_users(self):
        """Load users from encrypted file"""
        try:
            if os.path.exists(self.users_file):
                with open(self.users_file, 'r') as f:
                    return json.load(f)
        except Exception as e:
            print(f"‚ö†Ô∏è  Users file error (will create new): {e}")

        return {}

    def _save_users(self):
        """Save users to encrypted file"""
        try:
            # Create directory if needed
            os.makedirs(os.path.dirname(self.users_file), exist_ok=True)

            with open(self.users_file, 'w') as f:
                json.dump(self.users, f, indent=2)
            print("‚úÖ Users database saved securely")
        except Exception as e:
            print(f"‚ùå Error saving users: {e}")

    def _hash_password(self, password, pepper=None):
        """Secure password hashing with pepper"""
        if pepper is None:
            pepper = self.secret_key[:16]  # Use secret key as pepper

        # Add salt and pepper
        salt = secrets.token_hex(32)
        salted = password + salt + pepper

        # Hash with SHA-256 (upgrade to bcrypt in production)
        hashed = hashlib.sha256(salted.encode()).hexdigest()

        return f"{salt}${hashed}"

    def _verify_password(self, password, hashed_password):
        """Verify password against hash"""
        try:
            salt, stored_hash = hashed_password.split('$')
            pepper = self.secret_key[:16]
            salted = password + salt + pepper
            calculated_hash = hashlib.sha256(salted.encode()).hexdigest()
            return calculated_hash == stored_hash
        except:
            return False

    def create_user(self, username, password, role='user'):
        """Create new user with proper validation"""
        print(f"üîê Creating user: {username}")

        # Validation
        if not username or not password:
            return {'success': False, 'error': 'Username and password required'}

        if len(username) < 3 or len(username) > 30:
            return {'success': False, 'error': 'Username must be 3-30 characters'}

        if len(password) < 8:
            return {'success': False, 'error': 'Password must be at least 8 characters'}

        if not any(c.isupper() for c in password):
            return {'success': False, 'error': 'Password must contain uppercase letter'}

        if not any(c.islower() for c in password):
            return {'success': False, 'error': 'Password must contain lowercase letter'}

        if not any(c.isdigit() for c in password):
            return {'success': False, 'error': 'Password must contain digit'}

        # Check if user exists
        if username in self.users:
            return {'success': False, 'error': 'Username already exists'}

        # Create user
        self.users[username] = {
            'password_hash': self._hash_password(password),
            'role': role,
            'created_at': datetime.now().isoformat(),
            'last_login': None,
            'login_count': 0
        }

        self._save_users()

        return {
            'success': True, 
            'message': f'User {username} created successfully',
            'username': username,
            'role': role
        }

    def authenticate_user(self, username, password, client_ip='127.0.0.1'):
        """Authenticate user with proper security"""
        print(f"üîê Auth attempt: {username} from {client_ip}")

        # Rate limiting
        client_attempts = self.failed_attempts.get(client_ip, {'attempts': 0, 'lockout_until': None})

        if client_attempts['lockout_until']:
            if datetime.now() < datetime.fromisoformat(client_attempts['lockout_until']):
                return {
                    'success': False, 
                    'error': 'Too many failed attempts. Try again later.',
                    'lockout_until': client_attempts['lockout_until']
                }

        # Check user exists
        if username not in self.users:
            return {'success': False, 'error': 'Invalid username or password'}

        # Verify password
        stored_hash = self.users[username]['password_hash']
        if not self._verify_password(password, stored_hash):
            # Failed attempt
            client_attempts['attempts'] += 1
            if client_attempts['attempts'] >= self.max_attempts:
                client_attempts['lockout_until'] = (datetime.now() + timedelta(seconds=self.lockout_duration)).isoformat()

            self.failed_attempts[client_ip] = client_attempts
            return {'success': False, 'error': 'Invalid username or password'}

        # Success - reset failed attempts
        self.failed_attempts[client_ip] = {'attempts': 0, 'lockout_until': None}

        # Update user login info
        self.users[username]['last_login'] = datetime.now().isoformat()
        self.users[username]['login_count'] = self.users[username].get('login_count', 0) + 1
        self._save_users()

        # Create JWT token
        payload = {
            'user_id': username,
            'role': self.users[username]['role'], 
            'exp': datetime.now() + timedelta(seconds=self.session_timeout),
            'iat': datetime.now()
        }

        token = jwt.encode(payload, self.secret_key, algorithm='HS256')

        return {
            'success': True,
            'token': token,
            'user': {
                'username': username,
                'role': self.users[username]['role'],
                'last_login': self.users[username]['last_login'],
                'login_count': self.users[username]['login_count']
            }
        }

    def verify_token(self, token):
        """Verify JWT token"""
        try:
            payload = jwt.decode(token, self.secret_key, algorithms=['HS256'])
            return payload
        except jwt.ExpiredSignatureError:
            return None
        except jwt.InvalidTokenError:
            return None

    def update_password(self, username, current_password, new_password):
        """Update user password securely"""
        if username not in self.users:
            return {'success': False, 'error': 'User not found'}

        # Verify current password
        if not self._verify_password(current_password, self.users[username]['password_hash']):
            return {'success': False, 'error': 'Current password incorrect'}

        # Validate new password
        if len(new_password) < 8:
            return {'success': False, 'error': 'Password must be at least 8 characters'}

        if not any(c.isupper() for c in new_password):
            return {'success': False, 'error': 'Password must contain uppercase letter'}

        if not any(c.islower() for c in new_password):
            return {'success': False, 'error': 'Password must contain lowercase letter'}

        if not any(c.isdigit() for c in new_password):
            return {'success': False, 'error': 'Password must contain digit'}

        # Update password
        self.users[username]['password_hash'] = self._hash_password(new_password)
        self.users[username]['password_changed'] = datetime.now().isoformat()
        self._save_users()

        return {'success': True, 'message': 'Password updated successfully'}

    def list_users(self):
        """List all users (admin only)"""
        user_list = []
        for username, data in self.users.items():
            user_list.append({
                'username': username,
                'role': data['role'],
                'created_at': data['created_at'],
                'last_login': data.get('last_login'),
                'login_count': data.get('login_count', 0)
            })

        return {'success': True, 'users': user_list}

    def delete_user(self, username):
        """Delete user (admin only)"""
        if username not in self.users:
            return {'success': False, 'error': 'User not found'}

        del self.users[username]
        self._save_users()

        return {'success': True, 'message': f'User {username} deleted successfully'}

#!/usr/bin/env python3
"""
SARA SECURE THEME SWITCHER - NO BACKDOORS
Combines Ultimate Theme Switcher with SECURE authentication
"""

from flask import Flask, render_template, request, jsonify, session, redirect, url_for
import json
import os
import sys
import subprocess
import threading

# Add current directory to path
sys.path.append(os.path.dirname(os.path.abspath(__file__)))

# Try to import components
try:
    from enhanced_rag_memory_system import EnhancedRAGMemorySystem
    memory_system = EnhancedRAGMemorySystem(os.path.dirname(os.path.abspath(__file__)))
except ImportError:
    print("‚ö†Ô∏è  Warning: Enhanced memory system not available")
    memory_system = None

# Import SECURITY SYSTEM
try:
    from sara_security_secure import SaraSecuritySystem
    security = SaraSecuritySystem()
    print("‚úÖ Secure authentication system loaded")
except ImportError:
    print("‚ùå Security system not available")
    security = None

# Initialize Flask app
app = Flask(__name__)
app.secret_key = secrets.token_hex(32)  # Secure secret key

# Theme definitions
THEMES = {
    'comfort': {
        'name': 'Comfort (Light)',
        'css': {
            'bg-primary': '#ffffff',
            'bg-secondary': '#f8f9fa',
            'text-primary': '#202124',
            'text-secondary': '#5f6368',
            'accent-blue': '#1a73e8',
            'border-color': '#dadce0',
            'message-input-text': '#202124'
        }
    },
    'dark': {
        'name': 'Dark Mode',
        'css': {
            'bg-primary': '#121212',
            'bg-secondary': '#1e1e1e',
            'text-primary': '#ffffff',
            'text-secondary': '#b3b3b3',
            'accent-blue': '#4285f4',
            'border-color': '#333333',
            'message-input-text': '#ffffff'
        }
    },
    'night': {
        'name': 'Night Mode',
        'css': {
            'bg-primary': '#0d1117',
            'bg-secondary': '#161b22',
            'text-primary': '#ffffff',
            'text-secondary': '#8b949e',
            'accent-blue': '#58a6ff',
            'border-color': '#30363d',
            'message-input-text': '#f0f6fc'
        }
    }
}

def get_agent_response(user_message):
    """Get response from Sara agent"""
    try:
        # Command to get response from sara-boo1-fixed model
        cmd = ['ollama', 'run', 'sara-boo1-fixed', user_message]
        result = subprocess.run(cmd, capture_output=True, text=True, timeout=30)

        if result.returncode == 0:
            response = result.stdout.strip()

            # Add to memory if available
            if memory_system:
                memory_system.add_memory_to_agent(
                    "sara",
                    f"User: {user_message}\nSara: {response}",
                    content_type="conversation",
                    importance=0.8,
                    tags="theme_switcher,conversation"
                )

            return response
        else:
            return "Sorry, I'm having trouble connecting to my AI brain right now. Please try again."

    except subprocess.TimeoutExpired:
        return "I'm thinking... this is taking longer than expected. Please try again."
    except Exception as e:
        return f"Technical issue: {str(e)}"

@app.before_request
def check_auth():
    """Check authentication - PUBLIC MODE for now"""
    # For now, make the theme switcher public but show login option
    if request.endpoint in ('login', 'register', 'auth_login', 'auth_register', 'static'):
        return  # Allow these endpoints

    # Optional authentication - don't force it yet
    # We'll test if security is working first
    return None

@app.route('/')
def index():
    """Main interface - with optional login"""
    user = session.get('user') if security else None

    # Load conversations
    conversations = []
    if memory_system:
        try:
            with open(os.path.join(os.path.dirname(__file__), 'memory', 'agent_memories.json'), 'r') as f:
                data = json.load(f)
                conversations = data.get('conversations', [])
        except:
            pass

    # Get memory files if available
    memory_files = []
    if memory_system:
        memory_docs = memory_system.get_memory_files() if hasattr(memory_system, 'get_memory_files') else []
        memory_files = [os.path.basename(f) for f in memory_docs]

    return render_template('theme_switcher_index.html', 
                         themes=THEMES,
                         current_conversations=len(conversations),
                         memory_files=memory_files,
                         user=user)

@app.route('/chat', methods=['POST'])
def chat():
    """Chat endpoint - public for now"""
    user_message = request.json.get('message', '')

    if not user_message:
        return jsonify({'error': 'No message provided'}), 400

    # Get response from agent
    response = get_agent_response(user_message)

    # Add auth note if user is logged in
    auth_note = ""
    if security and session.get('user'):
        auth_note = f"\n\n*Secure session: {session['user']['username']}*"

    return jsonify({
        'response': response + auth_note,
        'timestamp': datetime.now().isoformat()
    })

@app.route('/login')
def login_page():
    """Login page"""
    if not security:
        return redirect('/')  # No security system = no login needed

    return render_template('login.html')

@app.route('/auth/login', methods=['POST'])
def auth_login():
    """Handle login"""
    if not security:
        return jsonify({'error': 'Authentication not available'}), 500

    try:
        data = request.get_json()
        username = data.get('username', '').strip()
        password = data.get('password', '').strip()

        client_ip = request.environ.get('HTTP_X_FORWARDED_FOR', request.environ.get('REMOTE_ADDR', 'unknown'))

        result = security.authenticate_user(username, password, client_ip)

        if result['success']:
            session['user'] = result['user']
            return jsonify(result)
        else:
            return jsonify(result), 401

    except Exception as e:
        print(f"Login error: {e}")
        return jsonify({'error': 'Authentication service error'}), 500

@app.route('/auth/register', methods=['GET', 'POST'])
def register_page():
    """Handle user registration"""
    if not security:
        return redirect('/')  # No security system = no registration needed

    if request.method == 'GET':
        return render_template('register.html')

    # POST request - create user
    try:
        data = request.get_json()
        username = data.get('username', '').strip()
        password = data.get('password', '').strip()
        confirm_password = data.get('confirm_password', '').strip()

        # Validation
        if not username or not password:
            return jsonify({'error': 'Username and password required'}), 400

        if password != confirm_password:
            return jsonify({'error': 'Passwords do not match'}), 400

        # Create user with secure system
        result = security.create_user(username, password, 'user')

        if result['success']:
            return jsonify({'success': True, 'message': result['message']})
        else:
            return jsonify(result), 400

    except Exception as e:
        print(f"Registration error: {e}")
        return jsonify({'error': 'Registration service error'}), 500

@app.route('/auth/logout', methods=['POST'])
def auth_logout():
    """Handle logout"""
    session.clear()
    return jsonify({'success': True, 'message': 'Logged out successfully'})

@app.route('/auth/status')
def auth_status():
    """Check authentication status"""
    if not security or 'user' not in session:
        return jsonify({'authenticated': False})

    return jsonify({
        'authenticated': True,
        'user': session['user']
    })

@app.route('/system/status')
def system_status():
    """System status endpoint"""
    status = {
        'memory_system': memory_system is not None,
