from flask import Flask, render_template, request, jsonify, session, redirect, url_for
import json
import os
import sys
import subprocess
import threading
import secrets  # ‚úÖ ADDED IMPORT
from datetime import datetime

# Add current directory to path
sys.path.append(os.path.dirname(os.path.abspath(__file__)))

# Try to import components
try:
    from enhanced_rag_memory_system import EnhancedRAGMemorySystem
    memory_system = EnhancedRAGMemorySystem(os.path.dirname(os.path.abspath(__file__)))
except ImportError:
    print("‚ö†Ô∏è  Warning: Enhanced memory system not available")
    memory_system = None

# Import SECURITY SYSTEM
try:
    from sara_security_secure import SaraSecuritySystem
    security = SaraSecuritySystem()
    print("‚úÖ Secure authentication system loaded")
except ImportError:
    print("‚ùå Security system not available")
    security = None

# Initialize Flask app
app = Flask(__name__)
app.secret_key = secrets.token_hex(32)  # ‚úÖ NOW IMPORTED!

# Theme definitions
THEMES = {
