arguments.new_string:
def _verify_password(self, password, stored_hash):
    """Verify password against hash - handles both formats"""
    try:
        pepper = self.secret_key[:16]

        # Check if old format (with salt) or new format
        if '$' in stored_hash:
            salt, old_hash = stored_hash.split('$')
            salted = password + salt + pepper
            calculated_hash = hashlib.sha256(salted.encode()).hexdigest()
            return calculated_hash == old_hash
        else:
            # New simple format
            calculated_hash = hashlib.sha256((password + pepper).encode()).hexdigest()
            return calculated_hash == stored_hash
    except:
        return False

