name: Run Agent Job

on:
  repository_dispatch:
    types: [job_created]

jobs:
  run-agent:
    runs-on: ubuntu-latest

    container:
      image: ghcr.io/${{ github.repository }}/agent:latest

    steps:
      - name: Checkout repository
        uses: actions/checkout@v4
        with:
          token: ${{ secrets.GITHUB_TOKEN }}
          ref: ${{ github.event.client_payload.branch }}

      - name: Setup environment
        env:
          ANTHROPIC_API_KEY: ${{ secrets.ANTHROPIC_API_KEY }}
          TELEGRAM_BOT_TOKEN: ${{ secrets.TELEGRAM_BOT_TOKEN }}
          OPENAI_API_KEY: ${{ secrets.OPENAI_API_KEY }}
          GITHUB_TOKEN: ${{ secrets.GITHUB_TOKEN }}
        run: |
          echo "Setting up autonomous agent environment..."
          echo "Job ID: ${{ github.event.client_payload.job_id }}"
          echo "Task: ${{ github.event.client_payload.task }}"

      - name: Execute Autonomous Agent
        env:
          JOB_ID: ${{ github.event.client_payload.job_id }}
          TASK: ${{ github.event.client_payload.task }}
          USER_ID: ${{ github.event.client_payload.user_id }}
          TIMESTAMP: ${{ github.event.client_payload.timestamp }}
        run: |
          python agent/main.py

      - name: Create Pull Request
        if: always()
        env:
          GITHUB_TOKEN: ${{ secrets.GITHUB_TOKEN }}
          JOB_ID: ${{ github.event.client_payload.job_id }}
        run: |
          if [ -f "changes.json" ]; then
            python agent/utils/create_pr.py
          else
            echo "No changes to commit"
          fi

      - name: Notify Completion
        if: always()
        env:
          TELEGRAM_BOT_TOKEN: ${{ secrets.TELEGRAM_BOT_TOKEN }}
          JOB_ID: ${{ github.event.client_payload.job_id }}
          USER_ID: ${{ github.event.client_payload.user_id }}
        run: |
          python agent/utils/notify.py

{
  "name": "autonomous-bot-event-handler",
  "version": "1.0.0",
  "description": "Event handler for autonomous AI bot",
  "main": "index.js",
  "scripts": {
    "start": "node index.js"
  },
  "dependencies": {
    "express": "^4.18.2",
    "body-parser": "^1.20.2",
    "node-telegram-bot-api": "^0.63.0",
    "@octokit/rest": "^19.0.13",
    "crypto-js": "^4.1.1"
  },
  "engines": {
    "node": ">=18.0.0"
  },
  "keywords": ["autonomous", "ai", "bot", "github-actions"],
  "author": "Sara AI",
  "license": "MIT"
}

const express = require('express');
const bodyParser = require('body-parser');
const TelegramBot = require('node-telegram-bot-api');
const { Octokit } = require('@octokit/rest');
require('dotenv').config();

const app = express();
const PORT = process.env.PORT || 8080;

// Telegram Bot
const bot = new TelegramBot(process.env.TELEGRAM_BOT_TOKEN);

// GitHub Client
const octokit = new Octokit({
  auth: process.env.GITHUB_TOKEN,
  owner: process.env.GITHUB_OWNER || 'your-username',
  repo: process.env.GITHUB_REPO || 'autonomous-bot'
});

// Allowed users for security
const ALLOWED_USERS = (process.env.ALLOWED_USERS || '').split(',').map(u => u.trim());

// Security: Verify user is allowed
function isUserAllowed(userId) {
  // If no allowed users specified, allow all (debug mode)
  if (!process.env.ALLOWED_USERS || ALLOWED_USERS.length === 0) {
    return true;
  }
  return ALLOWED_USERS.includes(userId.toString());
}

// Security: Filter sensitive information
function sanitizeTask(task) {
  // Remove potential sensitive patterns
  const cleaned = task
    .replace(/token\s*=\s*\S+/gi, 'token=REDACTED')
    .replace(/key\s*=\s*\S+/gi, 'key=REDACTED')
    .replace(/password\s*=\s*\S+/gi, 'password=REDACTED');

  return cleaned;
}

// Create autonomous job
async function createJob(userId, taskId, task) {
  const sanitizedTask = sanitizeTask(task);
  const jobData = {
    job_id: taskId,
    user_id: userId,
    task: sanitizedTask,
    timestamp: new Date().toISOString(),
    branch: `job-${taskId}-${Date.now()}`
  };

  try {
    // Create job branch
    await octokit.rest.git.createRef({
      owner: process.env.GITHUB_OWNER || 'your-username',
      repo: process.env.GITHUB_REPO || 'autonomous-bot',
      ref: `refs/heads/${jobData.branch}`,
      sha: (await octokit.rest.git.getRef({
        owner: process.env.GITHUB_OWNER || 'your-username',
        repo: process.env.GITHUB_REPO || 'autonomous-bot',
        ref: 'heads/main'
      })).data.object.sha
    });

    // Trigger GitHub Actions job
    await octokit.rest.repos.createDispatchEvent({
      owner: process.env.GITHUB_OWNER || 'your-username',
      repo: process.env.GITHUB_REPO || 'autonomous-bot',
      event_type: 'job_created',
      client_payload: jobData
    });

    console.log(`Job ${taskId} created for user ${userId}`);
    return jobData;

  } catch (error) {
    console.error('Error creating job:', error);
    throw error;
  }
}

// Generate unique job ID
function generateJobId() {
  return `job-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
}

// Handle Telegram messages
bot.on('message', async (msg) => {
  const chatId = msg.chat.id;
  const userId = msg.from.id;
  const task = msg.text;

  console.log(`Message from ${userId}: ${task}`);

  // Security check
  if (!isUserAllowed(userId)) {
    await bot.sendMessage(chatId, '‚ö†Ô∏è You are not authorized to use this bot.');
    return;
  }

  // Skip commands
  if (task.startsWith('/')) {
    return;
  }

  // Create and dispatch job
  const jobId = generateJobId();

  try {
    const jobData = await createJob(userId, jobId, task);

    await bot.sendMessage(
      chatId, 
      `ü§ñ Job created!\n\nüéØ Task: ${task}\nüìã Job ID: ${jobId}\n‚è∞ Started at: ${new Date().toLocaleString()}\n\nI'll notify you when it's completed!`
    );

  } catch (error) {
    await bot.sendMessage(
      chatId,
      `‚ùå Failed to create job: ${error.message}\n\nPlease try again later.`
    );
  }
});

// Handle commands
bot.onText(/\/start/, async (msg) => {
  const chatId = msg.chat.id;
  const userId = msg.from.id;

  if (!isUserAllowed(userId)) {
    await bot.sendMessage(chatId, '‚ö†Ô∏è Access denied.');
    return;
  }

  await bot.sendMessage(
    chatId,
    `ü§ñ Welcome to the Autonomous Bot!\n\n` +
    `I'm an AI agent that can help you with various tasks.\n\n` +
    `Just send me any task and I'll execute it autonomously!\n\n` +
    `Examples:\n‚Ä¢ "Create a simple Python script to..."` +
    `‚Ä¢ "Write a blog post about..."` +
    `‚Ä¢ "Fix the bug in the function..."` +
    `‚Ä¢ "Add a new feature to..."` +
    `\n\nReady to work! What would you like me to do?`
  );
});

bot.onText(/\/status/, async (msg) => {
  const chatId = msg.chat.id;

  try {
    const workflows = await octokit.rest.actions.listRepoWorkflows({
      owner: process.env.GITHUB_OWNER || 'your-username',
      repo: process.env.GITHUB_REPO || 'autonomous-bot'
    });

    let status = 'üìä *Bot Status*\n\n';

    for (const workflow of workflows.data.workflows) {
      const runs = await octokit.rest.actions.listWorkflowRuns({
        owner: process.env.GITHUB_OWNER || 'your-username',
        repo: process.env.GITHUB_REPO || 'autonomous-bot',
        workflow_id: workflow.id,
        per_page: 1
      });

      const latestRun = runs.data.workflow_runs[0];
      const statusEmoji = latestRun.conclusion === 'success' ? '‚úÖ' : latestRun.conclusion === 'failure' ? '‚ùå' : '‚è≥';

      status += `${statusEmoji} *${workflow.name}*\n   Status: ${latestRun.status}\n\n`;
    }

    await bot.sendMessage(chatId, status, { parse_mode: 'Markdown' });

  } catch (error) {
    await bot.sendMessage(chatId, `‚ùå Error checking status: ${error.message}`);
  }
});

// Express server for webhooks
app.use(bodyParser.json());

// GitHub webhook handler
app.post('/webhook', (req, res) => {
  console.log('Received webhook:', req.body);

  // Handle different webhook events
  if (req.headers['x-github-event']) {
    const event = req.headers['x-github-event'];
    console.log(`GitHub event: ${event}`);
  }

  res.status(200).send('OK');
});

// Health check endpoint
app.get('/health', (req, res) => {
  res.json({
    status: 'healthy',
    timestamp: new Date().toISOString(),
    allowed_users: ALLOWED_USERS.length
  });
});

// Start server
app.listen(PORT, () => {
  console.log(`üöÄ Event handler running on port ${PORT}`);
  console.log(`üì± Telegram bot initialized`);
  console.log(`üîê Security: ${ALLOWED_USERS.length} authorized users`);

  // Test connection
  bot.getMe().then(botInfo => {
    console.log(`ü§ñ Connected to Telegram bot: @${botInfo.username}`);
  });
});

module.exports = app;

