    user_message = request.json.get('message', '').strip()

    if not user_message:
        return jsonify({'error': 'No message provided'}), 400

    # Get context about what we're learning
    context = "You're in a learning session where I'm teaching you about your architecture and emergent capabilities."

    # Get response from real AI brain
    sara_response = get_ollama_response(user_message, context)

    # Save to learning database
    save_learning_conversation(user_message, sara_response)

    return jsonify({
        'response': sara_response,
        'timestamp': datetime.now().isoformat(),
        'model': 'Sara Learning Brain (Ollama)',
        'learning_mode': True
    })

@app.route('/status')
def status():
    """Sara's learning status"""
    return jsonify({
        'status': 'Learning Mode Active',
        'model': 'Ollama sara-boo1-simple:latest',
        'files': '471 total files',
        'capabilities': 'Full AI conversation and learning',
        'learning_sessions': 'Active database',
        'architecture': 'Multi-system integrated'
    })

if __name__ == '__main__':
