    <title>Sara AI - Local Only</title>
    <style>
        body { background: #1a1a1a; color: white; font-family: Arial; padding: 20px; }
        .container { max-width: 800px; margin: 0 auto; }
        .chat { height: 400px; overflow-y: auto; background: #2a2a2a; padding: 15px; border-radius: 10px; margin: 20px 0; }
        .input { display: flex; gap: 10px; }
        input { flex: 1; padding: 10px; border: none; border-radius: 5px; }
        button { padding: 10px 20px; background: #4dabf7; color: white; border: none; border-radius: 5px; cursor: pointer; }
        .message { margin: 10px 0; padding: 8px 12px; border-radius: 15px; }
        .user { background: #4dabf7; text-align: right; }
        .sara { background: #333; }
    </style>
</head>
<body>
    <div class="container">
        <h1>üè† Sara AI - Local Only (No Internet)</h1>
        <div class="chat" id="chat"></div>
        <div class="input">
            <input type="text" id="message" placeholder="Type your message..." />
            <button onclick="sendMessage()">Send</button>
        </div>
    </div>
    <script>
        function addMessage(text, isUser) {
            const chat = document.getElementById("chat");
            const div = document.createElement("div");
            div.className = "message " + (isUser ? "user" : "sara");
            div.textContent = text;
            chat.appendChild(div);
            chat.scrollTop = chat.scrollHeight;
        }
        async function sendMessage() {
            const input = document.getElementById("message");
            const text = input.value.trim();
            if (!text) return;

            addMessage(text, true);
            input.value = "";

            try {
                const response = await fetch("/chat", {
                    method: "POST",
                    headers: {"Content-Type": "application/json"},
                    body: JSON.stringify({message: text})
                });
                const data = await response.json();
                addMessage(data.response, false);
            } catch (e) {
                addMessage("Error: " + e.message, false);
            }
        }
        document.getElementById("message").addEventListener("keypress", (e) => {
            if (e.key === "Enter") sendMessage();
        });
    </script>
</body>
</html>
