#!/bin/bash

# Wake-up Call WITHOUT TTS - Multiple options
# Choose what you prefer: Music, Notifications, or Browser TTS

# Get the current date for logging
DATE=$(date +"%Y-%m-%d")
TIME=$(date +"%I:%M %p")
DAY=$(date +"%A")
LOGFILE="/home/godfather/wake-up-${DATE}.log"

echo "=== Wake-up Call Started at $(date) ===" >> $LOGFILE

# Function to wake up with music
wake_with_music() {
    echo "Using music wake-up..." >> $LOGFILE
    # You can replace this with your favorite song path
    if [ -f "/home/godfather/Music/wake-up.mp3" ]; then
        echo "Playing your wake-up song..." && paplay /home/godfather/Music/wake-up.mp3 &
    else
        # Create a simple wake-up sound
        echo "Playing wake-up chime..." && speaker-test -t 1 -f 1000 >> /dev/null 2>&1 &
    fi
}

# Function to show desktop notifications
show_notifications() {
    echo "Showing desktop notifications..." >> $LOGFILE

    # Weather notification
    WEATHER=$(curl -s "wttr.in/Raleigh?format=%l:+%c+%t" | head -1)
    notify-send -u critical "☀️ Weather" "$WEATHER"

    sleep 2

    # News notification
    TOP_STORY=$(timeout 5 curl -s "https://hacker-news.firebaseio.com/v0/topstories.json" | jq -r '.[0]')
    STORY=$(timeout 3 curl -s "https://hacker-news.firebaseio.com/v0/item/$TOP_STORY.json" | jq -r '.title // empty')
    if [[ -n "$STORY" && "$STORY" != "null" ]]; then
        notify-send -u critical "📰 Tech News" "$STORY"
    fi

    sleep 2

    # Stocks notification
    AAPL=$(timeout 3 curl -s "https://query1.finance.yahoo.com/v7/finance/chart/AAPL" | jq -r '.chart.result[0].meta.regularMarketPrice // empty')
    if [[ "$AAPL" != "null" && -n "$AAPL" ]]; then
        notify-send -u critical "📈 Markets" "Apple at $AAPL"
    fi

    sleep 2

    # Main wake-up notification
    notify-send -u critical "☕ Good Morning Boo!" "It's $TIME on $DAY. Time to start your day!"
}

# Function to use browser TTS (female voices available)
wake_with_browser() {
    echo "Using browser-based female voice..." >> $LOGFILE

    # Create HTML5 TTS page with female voice
    cat > /tmp/wake-up.html << 'EOF'
<!DOCTYPE html>
<html>
<head><title>Wake-up Call</title></head>
<body>
    <script>
        // Female voice setup
        const msg = new SpeechSynthesisUtterance("Good morning Boo! It's ${TIME} on ${DAY}. Ready to start your beautiful day?");
        msg.voice = speechSynthesis.getVoices().find(v => v.name.includes('Female') || v.name.includes('Samantha') || v.gender === 'female');
        msg.rate = 0.9;
        msg.pitch = 1.1;

        // Get weather
        fetch('https://wttr.in/Raleigh?format=%l:+%c+%t').then(r => r.text()).then(w => {
            const weather = new SpeechSynthesisUtterance("The weather update. " + w + ". Have a wonderful day ahead!");
            weather.voice = msg.voice;
            weather.rate = msg.rate;
            weather.pitch = msg.pitch;
            speechSynthesis.speak(weather);

            // Tech news
            setTimeout(() => {
                fetch('https://hacker-news.firebaseio.com/v0/topstories.json').then(r => r.json()).then(s => {
                    return fetch('https://hacker-news.firebaseio.com/v0/item/' + s[0] + '.json').then(r => r.json());
                }).then(story => {
                    if (story && story.title) {
                        const news = new SpeechSynthesisUtterance("Today in tech. " + story.title);
                        news.voice = msg.voice;
                        news.rate = msg.rate;
                        news.pitch = msg.pitch;
                        speechSynthesis.speak(news);
                    }
                }).catch(() => {
                    const fallback = new SpeechSynthesisUtterance("Tech stories are trending on Hacker News.");
                    fallback.voice = msg.voice;
                    speechSynthesis.speak(fallback);
                });
            }, 5000);

            // Stocks
            setTimeout(() => {
                fetch('https://query1.finance.yahoo.com/v7/finance/chart/AAPL').then(r => r.json()).then(d => {
                    const price = d.chart.result[0].meta.regularMarketPrice;
                    const stocks = new SpeechSynthesisUtterance("Market update. Apple at " + Math.round(price) + " dollars.");
                    stocks.voice = msg.voice;
                    stocks.rate = msg.rate;
                    stocks.pitch = msg.pitch;
                    speechSynthesis.speak(stocks);
                });
            }, 10000);

            // Closing
            setTimeout(() => {
                const closing = new SpeechSynthesisUtterance("That's your morning briefing. Go make it an amazing day, Boo!");
                closing.voice = msg.voice;
                closing.rate = msg.rate;
                closing.pitch = msg.pitch;
                speechSynthesis.speak(closing);
            }, 15000);
        });

        // Auto close after 30 seconds
        setTimeout(() => window.close(), 30000);
    </script>
</body>
</html>
EOF

    # Open in browser
    if command -v firefox 2>/dev/null; then
        firefox /tmp/wake-up.html &
    elif command -v google-chrome 2>/dev/null; then
        google-chrome --new-window /tmp/wake-up.html &
    elif command -v chromium 2>/dev/null; then
        chromium /tmp/wake-up.html &
    else
        echo "No browser found for TTS option" >> $LOGFILE
        return 1
    fi
}

# Function to play pre-recorded audio
wake_with_recording() {
    echo "Checking for pre-recorded wake-up..." >> $LOGFILE

    if [ -f "/home/godfather/wake-up-recording.wav" ] || [ -f "/home/godfather/wake-up-recording.mp3" ]; then
        echo "Playing pre-recorded wake-up..."
        if [ -f "/home/godfather/wake-up-recording.wav" ]; then
            paplay /home/godfather/wake-up-recording.wav &
        else
            paplay /home/godfather/wake-up-recording.mp3 &
        fi
    else
        echo "Create your wake-up recording first with:" | tee -a $LOGFILE
        echo "arecord -f cd -d 30 ~/wake-up-recording.wav" | tee -a $LOGFILE
        echo "Record yourself saying your wake-up message!" | tee -a $LOGFILE
    fi
}

# Main execution - Choose your wake-up type
WAKE_TYPE=${1:-"notification"}

echo "Wake-up type: $WAKE_TYPE" >> $LOGFILE

case $WAKE_TYPE in
    "music")
        wake_with_music
        show_notifications
        ;;
    "notification")
        show_notifications
        ;;
    "browser")
        wake_with_browser
        show_notifications
        ;;
    "recording")
        wake_with_recording
        show_notifications
        ;;
    *)
        echo "Usage: $0 [music|notification|browser|recording]"
        echo "Default: notification"
        ;;
esac

echo "=== Wake-up Call Completed at $(date) ===" >> $LOGFILE

# Summary to console
echo ""
echo "✅ Wake-up completed! Type: $WAKE_TYPE"
echo "Check $LOGFILE for details."
echo "Usage examples:"
echo "  $0 music         # Music + notifications"
echo "  $0 notification  # Desktop notifications only"
echo "  $0 browser       # Browser TTS with female voice"
echo "  $0 recording     # Your recorded voice"
echo ""

