#!/bin/bash
# Web Calendar Dashboard - Local, Mobile-Friendly Business Calendar

DATE=$(date +"%Y-%m-%d")
TIME=$(date +"%I:%M")
DASHBOARD_DIR="/home/godfather/calendar-dashboard"
PORT=8080

echo "🌐 Creating Web Calendar Dashboard..."
echo "📅 Mobile-friendly business calendar viewer"

# Create dashboard structure
mkdir -p "$DASHBOARD_DIR/scripts"
mkdir -p "$DASHBOARD_DIR/css"
mkdir -p "$DASHBOARD_DIR/data"

# Create main dashboard HTML
cat > "$DASHBOARD_DIR/index.html" <<CALENDAR_HTML
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Boo's Business Calendar Dashboard</title>
    <link rel="stylesheet" href="css/dashboard.css">
    <link rel="icon" href="data:image/svg+xml,<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 100'><text y='50%' text-anchor='middle' style='font-size:50px'>📅</text></svg>">
</head>
<body>
    <div class="container">
        <header class="header">
            <h1>📅 Boo's Business Dashboard</h1>
            <p class="subtitle">Schedule • 3D Printing • YouTube • Security • Automation</p>
            <div class="live-time">
                <span id="current-time">Loading...</span>
                <span class="current-date" id="current-date"></span>
            </div>
        </header>

        <section class="today-schedule">
            <h2>📋 Today's Business Activities</h2>
            <div id="today-schedule" class="schedule-grid">
                <div class="loading">Loading today's schedule...</div>
            </div>
        </section>

        <section class="current-activity">
            <h2>⏰ Current Activity</h2>
            <div id="current-activity" class="activity-card">
                <div class="time">--:--</div>
                <div class="activity">Loading...</div>
                <div class="status">Checking...</div>
            </div>
        </section>

        <section class="upcoming">
            <h2>🔔 Next Activities</h2>
            <div id="upcoming-activities" class="upcoming-list">
                <div class="loading">Loading upcoming...</div>
            </div>
        </section>

        <section class="business-status">
            <h2>🤖 Automation Status</h2>
            <div class="status-grid">
                <div class="status-item" id="status-wake-up">
                    <span class="icon">🌅</span>
                    <span class="label">Wake-up System</span>
                    <span class="state">--</span>
                </div>
                <div class="status-item" id="status-security">
                    <span class="icon">🛡️</span>
                    <span class="label">Security Scanner</span>
                    <span class="state">--</span>
                </div>
                <div class="status-item" id="status-3d">
                    <span class="icon">🖨️</span>
                    <span class="label">3D Production</span>
                    <span class="state">--</span>
                </div>
                <div class="status-item" id="status-youtube">
                    <span class="icon">🎬</span>
                    <span class="label">YouTube Content</span>
                    <span class="state">--</span>
                </div>
                <div class="status-item" id="status-mercari">
                    <span class="icon">💰</span>
                    <span class="label">Mercari Listings</span>
                    <span class="state">--</span>
                </div>
            </div>
        </section>

        <section class="focus-areas">
            <h2>🎯 Today's Focus Areas</h2>
            <div id="focus-areas" class="focus-grid">
                <div class="loading">Loading focus areas...</div>
            </div>
        </section>

        <section class="activity-log">
            <h2>📊 Today's Progress</h2>
            <div id="activity-log" class="log-container">
                <div class="loading">Loading activity log...</div>
            </div>
        </section>
    </div>

    <script src="scripts/dashboard.js"></script>
</body>
</html>
CALENDAR_HTML

# Create CSS styling
cat > "$DASHBOARD_DIR/css/dashboard.css" <<CALENDAR_CSS
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --primary: #2563eb;
    --secondary: #10b981;
    --warning: #f59e0b;
    --danger: #ef4444;
    --dark: #1f2937;
    --light: #f9fafb;
    --shadow: 0 4px 6px -1px rgba(0,0,0,0.1);
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: var(--dark);
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

.header {
    text-align: center;
    color: white;
    margin-bottom: 30px;
}

.header h1 {
    font-size: 3rem;
    margin-bottom: 0.5rem;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.subtitle {
    font-size: 1.2rem;
    opacity: 0.9;
    margin-bottom: 1rem;
}

.live-time {
    font-size: 1.5rem;
    font-weight: bold;
}

section {
    background: white;
    border-radius: 15px;
    padding: 25px;
    margin-bottom: 25px;
    box-shadow: var(--shadow);
}

section h2 {
    color: var(--primary);
    margin-bottom: 20px;
    font-size: 1.5rem;
}

.schedule-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 15px;
}

.activity-card {
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: white;
    padding: 20px;
    border-radius: 10px;
    text-align: center;
}

.activity-card .time {
    font-size: 2rem;
    font-weight: bold;
    margin-bottom: 10px;
}

.activity-card .activity {
    font-size: 1.2rem;
    margin-bottom: 10px;
}

.activity-card .status {
    font-size: 0.9rem;
    opacity: 0.9;
}

.status-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
}

.status-item {
    background: var(--light);
    border: 1px solid #e5e7eb;
    border-radius: 8px;
    padding: 15px;
    text-align: center;
    transition: transform 0.2s;
}

.status-item:hover {
    transform: translateY(-2px);
}

.status-item .icon {
    font-size: 2rem;
    display: block;
    margin-bottom: 5px;
}

.status-item .label {
    font-weight: bold;
    display: block;
    margin-bottom: 5px;
}

.status-item .state.active {
    color: var(--secondary);
}

.status-item .state.inactive {
    color: var(--danger);
}

.focus-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
}

.focus-item {
    background: var(--light);
    border-left: 4px solid var(--primary);
    padding: 15px;
    border-radius: 0 8px 8px 0;
    font-weight: 500;
}

.upcoming-list {
    max-height: 200px;
    overflow-y: auto;
}

.upcoming-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
    border-bottom: 1px solid #e5e7eb;
}

.upcoming-item:last-child {
    border-bottom: none;
}

.upcoming-time {
    font-weight: bold;
    color: var(--primary);
}

.log-container {
    max-height: 200px;
    overflow-y: auto;
    font-family: 'Courier New', monospace;
    font-size: 0.9rem;
    background: var(--dark);
    color: #10b981;
    padding: 15px;
    border-radius: 8px;
}

.loading {
    text-align: center;
    color: var(--secondary);
    font-style: italic;
}

/* Mobile Responsive */
@media (max-width: 768px) {
    .container {
        padding: 10px;
    }

    .header h1 {
        font-size: 2rem;
    }

    .schedule-grid,
    .status-grid,
    .focus-grid {
        grid-template-columns: 1fr;
    }

    section {
        padding: 15px;
    }
}

@media (max-width: 480px) {
    .header h1 {
        font-size: 1.5rem;
    }

    .subtitle {
        font-size: 1rem;
    }

    .live-time {
        font-size: 1.2rem;
    }
}

/* Accessibility */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

/* High contrast mode */
@media (prefers-contrast: high) {
    section {
        border: 2px solid var(--dark);
    }

    .status-item {
        border: 2px solid var(--dark);
    }
}
CALENDAR_CSS

# Create JavaScript for dashboard
cat > "$DASHBOARD_DIR/scripts/dashboard.js" <<CALENDAR_JS
// Business Calendar Dashboard JavaScript

class Dashboard {
    constructor() {
        this.updateInterval = 60000; // Update every minute
        this.loadCalendar();
        this.startLiveUpdates();
        this.updateDateTime();
        setInterval(() => this.updateDateTime(), 1000);
    }

    async loadCalendar() {
        try {
            const today = new Date().toISOString().split('T')[0];
            const response = await fetch(\`data/business-schedule-\${today}.json\`);
            const calendarData = await response.json();
            this.calendarData = calendarData;
            this.renderSchedule();
            this.renderFocusAreas();
        } catch (error) {
            console.error('Loading calendar:', error);
            this.displayError('Calendar data not found. Run ~/calendar-automation.sh');
        }
    }

    renderSchedule() {
        const scheduleContainer = document.getElementById('today-schedule');
        const routines = this.calendarData.daily_routines || {};

        const scheduleHTML = Object.entries(routines)
            .sort(([a], [b]) => a.localeCompare(b))
            .map(([time, activity]) => \`
                <div class="schedule-item">
                    <div class="time">\${time}</div>
                    <div class="activity">\${activity}</div></div>\`)
            .join('');

        scheduleContainer.innerHTML = scheduleHTML || '<div class="loading">No schedule data found</div>';
    }

    renderFocusAreas() {
        const focusContainer = document.getElementById('focus-areas');
        const focuses = this.calendarData.focus_areas || [];

        const focusHTML = focuses.map(focus => 
            \`<div class="focus-item">\${focus}</div>\`
        ).join('');

        focusContainer.innerHTML = focusHTML || '<div class="no-focus">No focus areas set</div>';
    }

    updateCurrentActivity() {
        const now = new Date();
        const currentTime = now.getHours().toString().padStart(2, '0') + ':' + 
                          now.getMinutes().toString().padStart(2, '0');

        const routines = this.calendarData.daily_routines || {};
        let currentActivity = 'No current activity';
        let status = 'inactive';

        // Find current or most recent activity
        const activities = Object.entries(routines)
            .sort(([a], [b]) => a.localeCompare(b))
            .map(([time, activity]) => ({ time, activity }));

        for (let activity of activities) {
            const [hour, minute] = activity.time.split(':').map(Number);
            const activityTime = new Date();
            activityTime.setHours(hour, minute, 0, 0);

            if (activityTime <= now) {
                currentActivity = activity.activity;
                status = 'active';
            }
        }

        // Update current activity display
        const currentElement = document.getElementById('current-activity');
        currentElement.innerHTML = \`
            <div class="time">\${currentTime}</div>
            <div class="activity">\${currentActivity}</div>
            <div class="status \${status}">\${status === 'active' ? '🟢 Active' : '⏸️ Waiting'}</div>
        \`;
    }

    updateUpcomingActivities() {
        const now = new Date();
        const routines = this.calendarData.daily_routines || {};

        // Find next activities
        const upcoming = Object.entries(routines)
            .filter(([time]) => {
                const [hour, minute] = time.split(':').map(Number);
                const activityTime = new Date();
                activityTime.setHours(hour, minute, 0, 0);
                return activityTime > now;
            })
            .sort(([a], [b]) => a.localeCompare(b))
            .slice(0, 3);

        const upcomingHTML = upcoming.map(([time, activity]) => \`
            <div class="upcoming-item">
                <span class="upcoming-time">\${time}</span>
                <span>\${activity}</span>
            </div>
        \`).join('');

        document.getElementById('upcoming-activities').innerHTML = 
            upcomingHTML || '<div class="no-upcoming">No upcoming activities today</div>';
    }

    updateAutomationStatus() {
        const statuses = {
            'status-wake-up': true, // Assuming active after setup
            'status-security': true,  // Running at 2 AM
            'status-3d': true,       // Scheduled
            'status-youtube': true,  // Scheduled
            'status-mercari': true   // Active
        };

        Object.entries(statuses).forEach(([id, active]) => {
            const element = document.getElementById(id);
            if (element) {
                const stateElement = element.querySelector('.state');
                if (stateElement) {
                    stateElement.className = 'state ' + (active ? 'active' : 'inactive');
                    stateElement.textContent = active ? '✅ Active' : '❌ Inactive';
                }
            }
        });
    }

    updateActivityLog() {
        const today = new Date().toISOString().split('T')[0];

        // Try to load today's log
