    'bg': '#ffffff',  # Clean white background
    'surface': '#f0f2f5',  # Facebook/Messenger style grey
    'surface_light': '#e4e6eb',  # Light grey borders
    'text_primary': '#050505',  # Soft black text
    'text_secondary': '#65676b',  # Medium grey for secondary text
    'accent': '#0077ff',  # Soft blue accent
    'accent_secondary': '#0066d6',  # Darker blue hover
    'message_user': '#0084ff',  # User message background
    'message_ai': '#ffffff',  # AI message background
    'border': '#e4e6eb',
    'success': '#4caf50',
    'status_active': '#e8f5e8',
    'status_text': '#2e7d32'
}

# Global variables for RAG system and agent interface
rag_system = None
agent_interface = None

class SaraRAGSystem:
    """Simple RAG memory system for Sara"""

    def __init__(self):
        self.conversations = []
        self.load_existing_conversations()

    def load_existing_conversations(self):
        """Load existing conversations from memory"""
        try:
            memory_file = os.path.expanduser("~/Desktop/sara/memory/agent_memories.json")
