class FileSystemManager:
    """File system operations for Sara"""

    def __init__(self):
        self.allowed_paths = [
            os.path.expanduser('~'),
            '/tmp',
            os.path.expanduser('~/Desktop'),
            os.path.expanduser('~/Downloads'),
            '/var/tmp'
        ]

    def is_safe_path(self, path: str) -> bool:
        """Check if path is safe to access"""
        real_path = os.path.realpath(path)
        for safe_path in self.allowed_paths:
            if real_path.startswith(os.path.realpath(safe_path)):
                return True
        return False

    def list_directory(self, path: str = '.') -> dict:
        """List directory contents safely"""
        try:
            if not self.is_safe_path(path):
                return {'success': False, 'error': 'Access to this path is not allowed'}

            path = os.path.expanduser(path)
            if not os.path.exists(path):
                return {'success': False, 'error': 'Path does not exist'}

            if not os.path.isdir(path):
                return {'success': False, 'error': 'Path is not a directory'}

            items = []
            for item in os.listdir(path):
                item_path = os.path.join(path, item)
                stat = os.stat(item_path)

                item_info = {
                    'name': item,
                    'path': item_path,
                    'size': stat.st_size,
                    'modified': datetime.fromtimestamp(stat.st_mtime).isoformat(),
                    'type': 'directory' if os.path.isdir(item_path) else 'file'
                }

                if os.path.isfile(item_path):
                    # File extension
                    ext = os.path.splitext(item)[1].lower()
                    if ext:
                        item_info['extension'] = ext

                items.append(item_info)

            return {
                'success': True,
                'path': path,
                'items': sorted(items, key=lambda x: (x['type'], x['name'].lower()))
            }

        except Exception as e:
            return {'success': False, 'error': str(e)}

    def read_file(self, path: str, max_size: int = 1024*1024) -> dict:
        """Read file contents safely"""
        try:
            if not self.is_safe_path(path):
                return {'success': False, 'error': 'Access to this file is not allowed'}

            path = os.path.expanduser(path)
            if not os.path.exists(path):
                return {'success': False, 'error': 'File does not exist'}

            if not os.path.isfile(path):
                return {'success': False, 'error': 'Not a file'}

            # Check file size
            if os.path.getsize(path) > max_size:
                return {'success': False, 'error': 'File too large to preview'}

            with open(path, 'r', encoding='utf-8', errors='replace') as f:
                content = f.read()

            # Detect if file is binary
            if '\0' in content:
                return {'success': False, 'error': 'Binary file - cannot preview'}

            return {
                'success': True,
                'content': content,
                'size': len(content),
                'encoding': 'utf-8'
            }

        except Exception as e:
            return {'success': False, 'error': str(e)}

    def get_file_info(self, path: str) -> dict:
        """Get file information"""
        try:
            if not self.is_safe_path(path):
                return {'success': False, 'error': 'Access denied'}

            path = os.path.expanduser(path)
            if not os.path.exists(path):
                return {'success': False, 'error': 'File does not exist'}

            stat = os.stat(path)
            return {
                'success': True,
                'path': path,
                'name': os.path.basename(path),
                'size': stat.st_size,
                'modified': datetime.fromtimestamp(stat.st_mtime).isoformat(),
                'type': 'directory' if os.path.isdir(path) else 'file',
                'extension': os.path.splitext(path)[1].lower() if os.path.isfile(path) else None,
                'is_text': False if os.path.isdir(path) else self._is_text_file(path),
                'permissions': oct(stat.st_mode)[-3:]
            }
        except Exception as e:
            return {'success': False, 'error': str(e)}

    def _is_text_file(self, path: str) -> bool:
        """Check if file is text-based"""
        text_extensions = {
            '.txt', '.md', '.py', '.js', '.html', '.css', '.json', '.xml',
            '.yml', '.yaml', '.ini', '.cfg', '.conf', '.log', '.csv',
            '.sh', '.bash', '.zsh', '.fish', '.rb', '.php', '.java',
            '.c', '.cpp', '.h', '.hpp', '.rs', '.go', '.ts', '.jsx'
        }
        ext = os.path.splitext(path)[1].lower()
        return ext in text_extensions

