def handle_filesystem_command(command):
    """Handle filesystem commands from chat"""
    parts = command.split(maxsplit=1)
    if len(parts) < 2:
        return jsonify({'response': "‚ùå **Invalid filesystem command. Usage:** `!ls [path]` or `!cat [file]`"})

    cmd, path = parts
    path = path.strip()

    if cmd.lower() == 'ls':
        result = file_manager.list_directory(path)
        if result['success']:
            items_str = "\n".join([f"{'üìÅ' if i['type'] == 'directory' else 'üìÑ'} {i['name']}" for i in result['items']])
            response = f"üìÇ **Directory Listing: `{path}`**\n\n{items_str}"
        else:
            response = f"‚ùå **List Failed**: {result['error']}"

    elif cmd.lower() == 'cat':
        result = file_manager.read_file(path)
        if result['success']:
            content = result['content'][:500] + "..." if len(result['content']) > 500 else result['content']
            response = f"üìÑ **File: `{path}`**\n\n```\n{content}\n```"
        else:
            response = f"‚ùå **Read Failed**: {result['error']}"

    else:
        response = f"‚ùå **Unknown Filesystem Command**: `{cmd}`. Available: `ls`, `cat`"

    rag_system.add_conversation(f"FS: {cmd} {path}", response, 'filesystem_operation')
    return jsonify({'response': response})

