addMessage(type, content, timestamp = Date.now()) {
    const messageDiv = document.createElement('div');
    messageDiv.className = `message ${type}`;

    const avatar = document.createElement('div');
    avatar.className = `avatar ${type}`;
    avatar.textContent = type === 'user' ? 'B' : 'S';

    const messageContent = document.createElement('div');
    messageContent.className = 'message-content';

    // Process content to handle code blocks and hide thoughts
    const processedContent = this.processMessageContent(content);
    messageContent.innerHTML = processedContent;

    const timeDiv = document.createElement('div');
    timeDiv.className = 'timestamp';
    timeDiv.textContent = new Date(timestamp).toLocaleTimeString();
    messageContent.appendChild(timeDiv);

    if (type === 'user') {
        // User: content first, then avatar on right
        messageDiv.appendChild(messageContent);
        messageDiv.appendChild(avatar);
    } else {
        // Sara: avatar first, then content on right
        messageDiv.appendChild(avatar);
        messageDiv.appendChild(messageContent);
    }

    this.messagesContainer.appendChild(messageDiv);
    this.scrollToBottom();
}

processMessageContent(content) {
    // Remove AI thoughts (text between  tags)
    let processed = content.replace(/\]*>[\s\S]*?<\/think>/gi, '');

    // Enhanced code block detection - check for code patterns
    const codePatterns = [
        /```(\w+)?\n([\s\S]*?)```/g,  // Standard code blocks
        /`([^`]+)`/g,                     // Inline code with backticks
        /\b(function|class|def|import|const|let|var|#!/bin/bash|#!/usr/bin/env)\s*\w+/g, // Common code starters
        /\b(echo|cd|ls|mkdir|rm|grep|find|curl|wget)\s+/g,  // Shell commands
        /\b(pip|npm|yarn|python3|node)\s+/g,  // Package/utility commands
    ];

    // Check if content contains code patterns
    let hasCode = false;
    codePatterns.forEach(pattern => {
        if (pattern.test(processed)) {
            hasCode = true;
        }
    });

    // If code detected or looks like code, wrap in terminal
    if (hasCode || this.looksLikeCode(processed)) {
        processed = this.wrapInTerminal(processed);
    }

    return processed;
}

looksLikeCode(content) {
    // Heuristic for code detection
    const codeIndicators = [
        /#!/bin\/bash|#!/usr\/bin\/env/,
        /\bdef\s+\w+\s*\(/,
        /\bclass\s+\w+\s*:/,
        /\bfunction\s+\w+\s*\(/,
        /import\s+\w+/,
        /const\s+\w+\s*=/,
        /\becho\s+/,
        /\bcurl\s+/,
        /\bwget\s+/,
        /\bpip\s+install/,
        /\bnpm\s+install/,
    ];

    return codeIndicators.some(pattern => pattern.test(content));
}

wrapInTerminal(content) {
    // Create terminal window
    const terminalId = 'terminal-' + Math.random().toString(36).substr(2, 9);

    return `<div class="terminal" id="${terminalId}">
        <div class="terminal-header">
            <div class="terminal-controls">
                <span class="terminal-control terminal-close"></span>
                <span class="terminal-control terminal-minimize"></span>
                <span class="terminal-control terminal-maximize"></span>
            </div>
            <div class="terminal-title">Terminal</div>
        </div>
        <div class="terminal-body">
            <pre class="terminal-content">${this.escapeHtml(content)}</pre>
        </div>
    </div>`;
}

escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
}

