@keyframes bounce {
    0%, 80%, 100% { transform: scale(0); }
    40% { transform: scale(1.0); }
}

/* Terminal styles */
.terminal {
    background: #1e1e1e;
    border: 1px solid #333;
    border-radius: 8px;
    margin: 0.5rem 0;
    font-family: 'Fira Code', 'Consolas', 'Monaco', monospace;
    font-size: 0.85rem;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.5);
    overflow: hidden;
}

.terminal-header {
    background: #2d2d2d;
    border-bottom: 1px solid #444;
    padding: 0.5rem 1rem;
    display: flex;
    align-items: center;
}

.terminal-controls {
    display: flex;
    gap: 0.5rem;
}

.terminal-control {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    display: inline-block;
}

.terminal-close { background: #ff5f57; }
.terminal-minimize { background: #ffbd2e; }
.terminal-maximize { background: #28ca42; }

.terminal-title {
    flex: 1;
    text-align: center;
    color: #999;
    font-size: 0.75rem;
    margin-right: 3rem; /* Balance the controls on left */
}

.terminal-body {
    padding: 1rem;
    max-height: 400px;
    overflow-y: auto;
}

.terminal-content {
    color: #00ff00;
    white-space: pre-wrap;
    word-wrap: break-word;
    margin: 0;
    line-height: 1.4;
}

/* Terminal scrollbar */
.terminal-body::-webkit-scrollbar {
    width: 8px;
}

.terminal-body::-webkit-scrollbar-track {
    background: #1e1e1e;
}

.terminal-body::-webkit-scrollbar-thumb {
    background: #444;
    border-radius: 4px;
}

.terminal-body::-webkit-scrollbar-thumb:hover {
    background: #555;
}

