#!/usr/bin/env python3
# ðŸ’¼ SARA ENTERPRISE WEB UI - Professional Commercial AI Assistant

from flask import Flask, render_template, request, jsonify, Response
import subprocess
import json
import os
from datetime import datetime

app = Flask(__name__)

# Professional colors matching Sara Enterprise GUI
pro_colors = {
    "bg": "#0a0e27",
    "surface": "#151935",
    "accent": "#00ffcc",
    "accent_secondary": "#6b5b95",
    "text_primary": "#ffffff",
    "text_secondary": "#94a3b8",
    "success": "#10b981",
    "warning": "#f59e0b",
    "error": "#ef4444",
    "glass": "#1a1f37",
    "border": "#2a3147"
}

@app.route('/')
def home():
    """Main web interface"""
    return render_template('index.html', colors=pro_colors)

@app.route('/api/send', methods=['POST'])
def send_message():
    """Process message and get AI response"""
    try:
        data = request.json
        message = data.get('message', '').strip()

        if not message or len(message) < 2:
            return jsonify({'error': 'Message too short'}), 400

        # Save user message to memory
        save_to_memory(message, is_outgoing=True)

        # Get AI response via agent interface
        response = get_agent_response(message)

        # Save AI response to memory
        save_to_memory(response, is_outgoing=False)

        return jsonify({
            'success': True,
            'response': response,
            'timestamp': datetime.now().strftime("%H:%M")
        })

    except Exception as e:
        return jsonify({'error': str(e)}), 500

@app.route('/api/history', methods=['GET'])
def get_history():
    """Get conversation history from memory"""
    try:
        cmd = [
            "python3",
            "/home/godfather/Desktop/sara/enhanced_rag_memory_system.py",
            "--history",
            "--agent", "sara",
            "--limit", "50"
        ]

        result = subprocess.run(
            cmd,
            capture_output=True,
            text=True,
            timeout=15,
            cwd="/home/godfather/Desktop/sara"
        )

        if result.returncode == 0:
            try:
                history = json.loads(result.stdout)
                return jsonify({'success': True, 'history': history})
            except json.JSONDecodeError:
                return jsonify({'success': True, 'history': []})
        else:
            return jsonify({'success': True, 'history': []})

    except Exception as e:
        return jsonify({'success': True, 'history': []})

