        })

def save_to_memory(message, is_outgoing):
    """Save message to RAG memory system (auto-save)"""
    try:
        timestamp = datetime.now().strftime("%H:%M")
        cmd = [
            "python3",
            "/home/godfather/Desktop/sara/enhanced_rag_memory_system.py",
            "--save",
            "--agent", "sara",
            "--message", message,
            "--sender", "user" if is_outgoing else "sara",
            "--timestamp", timestamp
