        subprocess.Popen(
            cmd,
            stdout=subprocess.DEVNULL,
            stderr=subprocess.DEVNULL,
            cwd="/home/godfather/Desktop/sara"
        )
    except Exception:
        # Silent fail - user shouldn't see memory errors
        pass

