#!/usr/bin/env python3
"""
Security Module for Sara AI - Authentication & Protection
Provides secure login, session management, and basic firewall rules
"""

import hashlib
import secrets
import jwt
import json
from datetime import datetime, timedelta
from functools import wraps

class SaraSecuritySystem:
    def __init__(self):
        self.secret_key = secrets.token_urlsafe(32)
        self.session_timeout = 3600  # 1 hour
        self.failed_attempts = {}
        self.max_attempts = 5
        self.lockout_duration = 900  # 15 minutes

        # Default admin credentials (change in production)
        self.admin_credentials = {
            'username': 'admin',
            'password_hash': self._hash_password('sara123'),
            'role': 'admin'
        }

        # Load users from file if exists
        self.users = self._load_users()

    def _hash_password(self, password):
        """Hash password with salt"""
        salt = secrets.token_hex(16)
        password_hash = hashlib.pbkdf2_hmac('sha256', 
                                          password.encode('utf-8'), 
                                          salt.encode('utf-8'), 
                                          100000)
        return f"{salt}${password_hash.hex()}"

    def _verify_password(self, password, stored_hash):
        """Verify password against stored hash"""
        try:
            salt, hash_value = stored_hash.split('$')
            password_hash = hashlib.pbkdf2_hmac('sha256',
                                              password.encode('utf-8'),
                                              salt.encode('utf-8'),
                                              100000)
            return password_hash.hex() == hash_value
        except:
            return False

    def _load_users(self):
        """Load users from JSON file"""
        try:
            with open('/home/godfather/Desktop/sara/users.json', 'r') as f:
                return json.load(f)
        except:
            # Create default users file
            default_users = {
                self.admin_credentials['username']: {
                    'password_hash': self.admin_credentials['password_hash'],
                    'role': self.admin_credentials['role'],
                    'created_at': datetime.now().isoformat(),
                    'last_login': None
                }
            }
            self._save_users(default_users)
            return default_users

    def _save_users(self, users=None):
        """Save users to JSON file"""
        if users is None:
            users = self.users

        with open('/home/godfather/Desktop/sara/users.json', 'w') as f:
            json.dump(users, f, indent=2)

    def _is_ip_locked(self, client_ip):
        """Check if IP is locked out due to failed attempts"""
        if client_ip in self.failed_attempts:
            attempts = self.failed_attempts[client_ip]
            if attempts['count'] >= self.max_attempts:
                lock_until = attempts['timestamp'] + self.lockout_duration
                if datetime.now().timestamp() < lock_until:
                    return True
                else:
                    # Reset lockout
                    del self.failed_attempts[client_ip]
                    return False
        return False

    def _record_failed_attempt(self, client_ip):
        """Record a failed login attempt"""
        timestamp = datetime.now().timestamp()
        if client_ip not in self.failed_attempts:
            self.failed_attempts[client_ip] = {'count': 1, 'timestamp': timestamp}
        else:
            self.failed_attempts[client_ip]['count'] += 1
            self.failed_attempts[client_ip]['timestamp'] = timestamp

    def create_session_token(self, user_data):
        """Create JWT session token"""
        payload = {
            'user_id': user_data['username'],
            'role': user_data.get('role', 'user'),
            'exp': datetime.utcnow() + timedelta(seconds=self.session_timeout),
            'iat': datetime.utcnow()
        }
        return jwt.encode(payload, self.secret_key, algorithm='HS256')

    def verify_session_token(self, token):
        """Verify JWT session token"""
        try:
            payload = jwt.decode(token, self.secret_key, algorithms=['HS256'])
            return payload
        except jwt.ExpiredSignatureError:
            return None
        except jwt.InvalidTokenError:
            return None

    def authenticate_user(self, username, password, client_ip):
        """Authenticate user credentials"""
        # Check IP lockout
        if self._is_ip_locked(client_ip):
            return {
                'success': False,
                'error': 'Too many failed attempts. IP temporarily locked. Try again later.'
            }

        # Check credentials
        if username in self.users:
            user_data = self.users[username]
            if self._verify_password(password, user_data['password_hash']):
                # Update last login
                self.users[username]['last_login'] = datetime.now().isoformat()
                self._save_users()

                # Create session token
                session_token = self.create_session_token(user_data)

                return {
                    'success': True,
                    'token': session_token,
                    'user': {
                        'username': username,
                        'role': user_data.get('role', 'user'),
                        'last_login': user_data['last_login']
                    },
                    'redirect': '/'
                }

        # Record failed attempt
        self._record_failed_attempt(client_ip)

        return {
            'success': False,
            'error': 'Invalid username or password'
        }

    def create_user(self, username, password, role='user'):
        """Create new user account"""
        if username in self.users:
            return {
                'success': False,
                'error': 'Username already exists'
            }

        self.users[username] = {
            'password_hash': self._hash_password(password),
            'role': role,
            'created_at': datetime.now().isoformat(),
            'last_login': None
        }

        self._save_users()

        return {
            'success': True,
            'message': f'User {username} created successfully'
        }

    def change_password(self, username, old_password, new_password):
        """Change user password"""
        if username not in self.users:
            return {
                'success': False,
                'error': 'User not found'
            }

        user_data = self.users[username]
        if not self._verify_password(old_password, user_data['password_hash']):
            return {
                'success': False,
                'error': 'Current password is incorrect'
            }

        user_data['password_hash'] = self._hash_password(new_password)
        self._save_users()

        return {
            'success': True,
            'message': 'Password changed successfully'
        }

# Decorator for protected routes
def require_auth(f):
    @wraps(f)
    def decorated_function(*args, **kwargs):
        from flask import request, jsonify, session, redirect, url_for

        token = None

        # Check token in various places
        if hasattr(request, 'headers') and 'Authorization' in request.headers:
            token = request.headers['Authorization'].split(' ')[1] if len(request.headers['Authorization'].split(' ')) > 1 else None
        elif request.args.get('token'):
            token = request.args.get('token')
        elif session.get('auth_token'):
            token = session.get('auth_token')

        if not token:
            return jsonify({'error': 'Authentication required'}), 401

        # Verify token
        import os
        sys.path.append('/home/godfather/Desktop/sara')
        from sara_security import SaraSecuritySystem

        security = SaraSecuritySystem()
        payload = security.verify_session_token(token)

        if not payload:
            return jsonify({'error': 'Invalid or expired token'}), 401

        # Add user data to request context
        request.current_user = payload
        return f(*args, **kwargs)

    return decorated_function

