#!/usr/bin/env python3
"""
Sara Theme Switcher Web Interface - Clean Version
Fixed authentication, no broken decorators
"""

from flask import Flask, render_template, request, jsonify, session, redirect, url_for
import sys
import os
import json
import subprocess
from datetime import datetime
import threading

# Add current directory to path for imports
sys.path.insert(0, os.path.dirname(os.path.abspath(__file__)))

app = Flask(__name__)
app.secret_key = 'sara_theme_switcher_2026_secret_key'

# Import code formatter
from code_formatter import add_code_highlighting

# Initialize systems
rag_system = SaraRAGSystem()
agent_interface = SaraAgentInterface()

# Initialize security systems
try:
    from sara_security import SaraSecuritySystem

    security_system = SaraSecuritySystem()
    print("Security systems loaded successfully")

except ImportError as e:
    print(f"Security module not available: {e}")
    security_system = None

# Clean authentication check - no decorators
def check_authentication():
    """Check if user is authenticated"""
    if not security_system:
        return True  # No security system = allow access

    if 'user' not in session:
        return False

    return True

# Routes
@app.route('/')
def index():
    """Main chat interface"""
    if not check_authentication():
        return redirect('/login')

    # Get conversations and memory
    try:
        conversations = rag_system.get_conversations(limit=50)
        memory_files = rag_system.get_memory_files()
    except:
        conversations = []
        memory_files = []

    return render_template('theme_switcher_index.html', 
                         conversations=conversations, 
                         memory_files=memory_files)

@app.route('/login')
def login_page():
    """Serve login page"""
    if security_system and 'user' in session:
        return redirect('/')  # Already logged in
    return render_template('login.html')

@app.route('/auth/register', methods=['GET', 'POST'])
def register_page():
    """Handle user registration"""
    if request.method == 'GET':
        return render_template('register.html')

    try:
        data = request.get_json()
        username = data.get('username', '').strip()
        password = data.get('password', '').strip()
        confirm_password = data.get('confirm_password', '').strip()

        # Validation
        if not username or not password:
            return jsonify({'error': 'Username and password required'}), 400

        if password != confirm_password:
            return jsonify({'error': 'Passwords do not match'}), 400

        if len(username) < 3 or len(username) > 20:
            return jsonify({'error': 'Username must be 3-20 characters'}), 400

        if len(password) < 6:
            return jsonify({'error': 'Password must be at least 6 characters'}), 400

        # Create user
        result = security_system.create_user(username, password, 'user')

        if result['success']:
            return jsonify({
                'success': True, 
                'message': 'User created successfully! Please login.'
            })
        else:
            return jsonify(result), 400

    except Exception as e:
        print(f"Registration error: {e}")
        return jsonify({'error': 'Registration service error'}), 500

@app.route('/auth/login', methods=['POST'])
def auth_login():
    """Handle authentication"""
    if not security_system:
        return jsonify({'error': 'Security system not available'}), 500

    try:
        data = request.get_json()
        username = data.get('username', '').strip()
        password = data.get('password', '').strip()

        if not username or not password:
            return jsonify({'error': 'Please enter both username and password'}), 400

        # Get client IP for rate limiting
        client_ip = request.environ.get('HTTP_X_FORWARDED_FOR', request.environ.get('REMOTE_ADDR', 'unknown'))

        # Authenticate user
        result = security_system.authenticate_user(username, password, client_ip)

        if result['success']:
            # Store user in session (simple and clean)
            session['user'] = result['user']

            return jsonify(result)
        else:
            return jsonify(result), 401

    except Exception as e:
        print(f"Login error: {e}")
        return jsonify({'error': 'Authentication service error'}), 500

@app.route('/auth/logout', methods=['POST'])
def auth_logout():
    """Handle logout"""
    session.clear()
    return jsonify({'success': True, 'message': 'Logged out successfully'})

@app.route('/auth/status')
def auth_status():
    """Check authentication status"""
    if 'user' in session:
        return jsonify({
            'authenticated': True,
            'user': session['user']
        })
    return jsonify({'authenticated': False})

# Protected routes - simple auth check
@app.route('/chat', methods=['POST'])
def chat():
    """Handle chat messages with enhanced features"""
    if not check_authentication():
        return jsonify({'error': 'Authentication required'}), 401

    try:
        data = request.get_json()
        message = data.get('message', '').strip()

        if not message:
            return jsonify({'error': 'Message is required'}), 400

        # Get recent context
        recent_context = rag_system.get_recent_context(limit=5)

        # Format context for prompt
        context_text = "\n".join([
            f"User: {conv['user']}\nSara: {conv['agent']}" 
            for conv in recent_context
        ])

        # Get enhanced AI response
        ai_response = agent_interface.get_response(message, context_text)

        # Store in RAG memory
        rag_system.add_conversation(message, ai_response)

        # Format code blocks in response
        formatted_response = add_code_highlighting(ai_response)

        return jsonify({
            'response': formatted_response,
            'timestamp': datetime.now().isoformat(),
            'conversation_count': len(rag_system.conversations),
            'session_id': session.get('session_id'),
            'agent_model': agent_interface.default_model
        })

    except Exception as e:
        print(f"Chat error: {e}")
        return jsonify({'error': 'Internal server error'}), 500

@app.route('/conversations')
def get_conversations():
    """Get conversations list"""
    if not check_authentication():
        return jsonify({'error': 'Authentication required'}), 401

    try:
        limit = min(request.args.get('limit', 50, type=int), 100)
        offset = request.args.get('offset', 0, type=int)
        conversations = rag_system.conversations[offset:offset+limit]

        return jsonify({
            'conversations': conversations,
            'total': len(rag_system.conversations),
            'offset': offset,
            'limit': limit
        })

    except Exception as e:
        return jsonify({'error': str(e)}), 500

@app.route('/memory_files')
def memory_files():
    """Get enhanced memory files list"""
    if not check_authentication():
        return jsonify({'error': 'Authentication required'}), 401

    try:
        files = rag_system.get_memory_files()
        return jsonify({'files': files, 'total': len(files)})
    except Exception as e:
        return jsonify({'error': str(e)}), 500

@app.route('/tasks')
def manage_tasks():
    """Task management endpoint"""
    if not check_authentication():
        return jsonify({'error': 'Authentication required'}), 401

    try:
        action = request.args.get('action', 'list')

        if action == 'list':
            return jsonify({'tasks': [], 'total': 0})
        elif action == 'add':
            title = request.args.get('title', '').strip()
            if title:
                return jsonify({'success': True, 'message': f'Task "{title}" added'})
            return jsonify({'error': 'Title required'}), 400
        else:
            return jsonify({'error': 'Invalid action'}), 400

    except Exception as e:
        return jsonify({'error': str(e)}), 500

@app.route('/theme_info')
def theme_info():
    """Get theme information"""
    return jsonify({
        'themes': ['Comfort', 'Dark', 'Night'],
        'current_theme': session.get('theme', 'Comfort')
    })

@app.route('/set_theme', methods=['POST'])
def set_theme():
    """Set theme preference"""
    try:
        theme = request.json.get('theme', 'Comfort')
        if theme in ['Comfort', 'Dark', 'Night']:
            session['theme'] = theme
            return jsonify({'success': True, 'theme': theme})
        return jsonify({'error': 'Invalid theme'}), 400
    except:
        return jsonify({'error': 'Theme setting failed'}), 500

if __name__ == '__main__':
    print("Starting Sara Theme Switcher Web Interface...")
    print("=" * 65)

    # Initialize RAG memory system
    try:
        rag_system = SaraRAGSystem()
        print(f"Loaded {len(rag_system.conversations)} existing conversations")
    except Exception as e:
        print(f"Error loading conversations: {e}")
        rag_system = SaraRAGSystem()
        rag_system.conversations = []

    agent_interface = SaraAgentInterface()

    print("Available themes: Comfort (Light), Dark, Night")
    print("=" * 65)

    # Start the Flask app
    try:
        app.run(host='10.211.144.110', port=8890, debug=False, threaded=True)
    except KeyboardInterrupt:
        print("\nShutting down Sara Theme Switcher Web Interface...")
    except Exception as e:
        print(f"Error starting web interface: {e}")

#!/usr/bin/env python3
"""
Enhanced RAG Memory System - Clean Version
"""

import json
import os
from datetime import datetime, timedelta
import glob
from pathlib import Path

class SaraRAGSystem:
    def __init__(self, memory_dir="memory", max_conversations=10000):
        self.memory_dir = Path(memory_dir)
        self.memory_dir.mkdir(exist_ok=True)
        self.conversations = []
        self.max_conversations = max_conversations
        self.load_conversations()

    def load_conversations(self):
        """Load all conversations from memory files"""
        try:
            # Check for a conversations file
            conv_file = self.memory_dir / "conversations.json"
            if conv_file.exists():
                with open(conv_file, 'r', encoding='utf-8') as f:
                    self.conversations = json.load(f)
                    # Ensure old format data gets new fields
                    for conv in self.conversations:
                        conv.setdefault('session_id', 'legacy')
                        conv.setdefault('message_id', conv.get('message_id', f"{datetime.now().strftime('%Y%m%d_%H%M%S')}_{len(self.conversations)}"))
                        conv.setdefault('topics', [])
                        conv.setdefault('importance', 1.0)
                print(f"Loaded {len(self.conversations)} existing conversations")
            else:
                self.conversations = []
        except Exception as e:
            print(f"Error loading conversations: {e}")
            self.conversations = []

    def save_conversations(self):
        """Save conversations to file"""
        try:
            conv_file = self.memory_dir / "conversations.json"
            # Save last 5000 conversations to manage file size
            to_save = self.conversations[-5000:] if len(self.conversations) > 5000 else self.conversations
            with open(conv_file, 'w', encoding='utf-8') as f:
                json.dump(to_save, f, indent=2, default=str)
        except Exception as e:
            print(f"Error saving conversations: {e}")

    def get_memory_files(self):
        """Get list of all memory files"""
        try:
            pattern = str(self.memory_dir / "*.md")
            files = []
            for file_path in glob.glob(pattern):
                if os.path.exists(file_path):
                    stat = os.stat(file_path)
                    files.append({
                        'name': os.path.basename(file_path),
                        'path': file_path,
                        'size': stat.st_size,
                        'modified': datetime.fromtimestamp(stat.st_mtime).isoformat()
                    })
            return sorted(files, key=lambda x: x['modified'], reverse=True)
        except:
            return []

    def add_conversation(self, user_message, ai_response):
        """Add new conversation with enhanced data"""
        conversation = {
            'timestamp': datetime.now().isoformat(),
            'user': user_message,
            'agent': ai_response,
            'agent_name': 'sara',
            'date': datetime.now().strftime('%Y-%m-%d'),
            'topics': [],
            'importance': 1.0,
            'session_id': session.get('session_id', 'default') if 'session' in globals() else 'default',
            'message_id': f"{datetime.now().strftime('%Y%m%d_%H%M%S')}_{len(self.conversations)}"
        }
        self.conversations.append(conversation)
        self.save_conversations()

    def get_recent_context(self, limit=10):
        """Get recent conversations for context"""
        return self.conversations[-limit:] if self.conversations else []

    def search_conversations(self, query, limit=50):
        """Search conversations"""
        query_lower = query.lower()
        results = []

        for conv in self.conversations:
            score = 0
            if query_lower in conv['user'].lower():
                score += 10
            if query_lower in conv['agent'].lower():
                score += 10

            if score > 0:
                results.append({
                    'conversation': conv,
                    'score': score
                })

        # Sort by score and return top results
        results.sort(key=lambda x: x['score'], reverse=True)
        return [r['conversation'] for r in results[:limit]]

    def get_conversations(self, limit=50, offset=0):
        """Get paginated conversations"""
        convs = self.conversations[::-1]  # Reverse for newest first
        return convs[offset:offset+limit]

