import urllib.request
import urllib.parse
import json

class SaraAgentInterface:
    def __init__(self):
        self.agent_url = "http://localhost:11434"
        self.default_model = "sara-boo1-fixed:latest"
        self.timeout = 30

    def get_response(self, message, context="", prompt_enhancement=True):
        """Get enhanced response from AI agent"""
        try:
            # Construct enhanced prompt
            if prompt_enhancement:
                enhanced_prompt = f"""You are Sara, a premium AI assistant with theme switching capabilities.
Recent conversation context:
{context}

Current user message: {message}

Please provide a helpful, friendly, and concise response. Keep responses conversational and engaging.
If you provide code, wrap it in proper formatting with ``` at start and end.
If you can execute commands or take actions, offer to do so explicitly.
Note: The user has a clean autism-friendly interface - keep responses streamlined.
"""
            else:
                enhanced_prompt = f"Context: {context}\n\nUser: {message}\n\nSara (friendly assistant):"

            # Prepare the request
            data = {
                "model": self.default_model,
                "prompt": enhanced_prompt,
                "stream": False,
                "options": {
                    "temperature": 0.7,
                    "top_p": 0.9,
                    "max_tokens": 1000
                }
            }

            json_data = json.dumps(data).encode('utf-8')

            # Make the request with timeout
            req = urllib.request.Request(
                f"{self.agent_url}/api/generate",
                data=json_data,
                headers={'Content-Type': 'application/json'}
            )

            with urllib.request.urlopen(req, timeout=self.timeout) as response:
                result = json.loads(response.read().decode('utf-8'))
                return result.get('response', "I'm having trouble processing that right now.")

