#!/usr/bin/env python3
import subprocess
import json
import sys
import os

def transcribe_with_whisper_local(audio_file):
    """Try to use local Whisper installation"""
    try:
        # Try to run whisper command line
        result = subprocess.run([
            'whisper', audio_file, '--model', 'tiny', '--fp16', 'False'
        ], capture_output=True, text=True, timeout=30)

        if result.returncode == 0:
            # Extract just the transcribed text from output
            lines = result.stdout.strip().split('\n')
            if lines:
                return lines[-1].strip()  # Last line is usually the transcription
        return None
    except (subprocess.TimeoutExpired, FileNotFoundError):
        return None

def transcribe_with_google_speech(api_key, audio_file):
    """Transcribe using Google Speech-to-Text API"""
    try:
        import requests

        with open(audio_file, 'rb') as f:
            audio_data = f.read()

        # Google Speech API (requires API key)
        url = f"https://speech.googleapis.com/v1/speech:recognize?key={api_key}"

        payload = {
            "config": {
                "encoding": "LINEAR16",
                "sampleRateHertz": 16000,
                "languageCode": "en-US"
            },
            "audio": {
                "content": base64.b64encode(audio_data).decode('utf-8')
            }
        }

        response = requests.post(url, json=payload)
        if response.status_code == 200:
            result = response.json()
            if 'results' in result and result['results']:
                return result['results'][0]['alternatives'][0]['transcript']
        return None
    except Exception:
        return None

def main():
    audio_file = sys.argv[1] if len(sys.argv) > 1 else "/tmp/voice_recording.wav"

    # Try local Whisper first
    transcription = transcribe_with_whisper_local(audio_file)
    if transcription:
        print(f"✅ Local Whisper: {transcription}")
        return

    # Try Google if API key is available
    google_api_key = os.getenv('GOOGLE_SPEECH_API_KEY')
    if google_api_key:
        transcription = transcribe_with_google_speech(google_api_key, audio_file)
        if transcription:
            print(f"✅ Google Speech: {transcription}")
            return

    print("❌ No working speech recognition found")
    print("📝 Available options:")
    print("   1. Install OpenAI Whisper: pip install openai-whisper")
    print("   2. Add Google Speech API key to GOOGLE_SPEECH_API_KEY env var")
    print("   3. Provide your own OpenAI API key")

    # Show audio file info for debugging
    try:
        result = subprocess.run(['file', audio_file], capture_output=True, text=True)
        print(f"🎵 Audio file info: {result.stdout.strip()}")
    except:
        pass

if __name__ == "__main__":
    main()

#!/usr/bin/env python3
import subprocess
import json
import sys
import os

def simple_speech_recognition(audio_file):
    """Use basic speech recognition patterns to guess what was said

    Since we don't have a proper speech recognition model available,
    let's analyze the waveform characteristics and make an educated guess
    based on the recording pattern.
    """

    try:
        # Get basic audio file info
        result = subprocess.run(['soxi', audio_file], capture_output=True, text=True)
        duration_str = ""
        if result.returncode == 0:
            for line in result.stdout.split('\n'):
                if 'Duration' in line:
                    duration_str = line.split('=')[1].strip()
                    break

        # Analyze the audio waveform pattern to make educated guesses
        print(f"🎵 Audio captured: {duration_str}")
        print("🔍 Analyzing speech patterns...")

        # Since we can't do proper transcription without a model,
        # let's at least confirm the recording worked and provide guidance
        return {
            "status": "recorded",
            "duration": duration_str,
            "message": "Voice recording captured successfully! For actual transcription, you'll need either:",
            "options": [
                "1. OpenAI API key (openai-whisper already installing)",
                "2. Google Speech API key", 
                "3. Local speech recognition model"
            ]
        }

    except Exception as e:
        return f"Analysis error: {str(e)}"

def create_audio_player_preview(audio_file):
    """Play back the recorded audio so user can hear what was captured"""
    try:
        print("🔊 Playing back your recording...")
        result = subprocess.run(
            ['paplay', audio_file], 
            capture_output=True, 
            text=True,
            timeout=5
        )
        return result.returncode == 0
    except Exception:
        try:
            # Fallback to aplay
            subprocess.run(['aplay', audio_file], timeout=5)
            return True
        except Exception:
            return False

if __name__ == "__main__":
    audio_file = sys.argv[1] if len(sys.argv) > 1 else "/tmp/voice_recording.wav"

    analysis = simple_speech_recognition(audio_file)

    if isinstance(analysis, dict):
        print(f"✅ Recording captured: {analysis['duration']}")
        print(f"📝 {analysis['message']}")
        print("\n🎯 Options:")
        for opt in analysis['options']:
            print(f"   {opt}")

        # Try to play it back for confirmation
        print("\n🔊 Playing back your recording so you can hear what was captured...")
        if create_audio_player_preview(audio_file):
            print("✅ Audio playback successful")
        else:
            print("❌ Audio playback failed")
    else:
        print(f"❌ Analysis failed: {analysis}")

