#!/usr/bin/env python3
"""
AI Advisor Connection System
Sara (Main) + Local AI Advisor (Qwen2.5) - Enhanced Trading Analysis
"""

import subprocess
import json
import time
import ollama

class AIAdvisor:
    def __init__(self):
        self.advisor_model = "qwen2.5:7b"
        self.is_available = False
        self.last_response_time = 0

    def check_availability(self):
        """Check if advisor AI is available"""
        try:
            # Try to list models
            result = ollama.list()
            if 'models' in result:
                for model in result['models']:
                    if self.advisor_model in model.get('name', ''):
                        self.is_available = True
                        return True
            return False
        except Exception as e:
            print(f"Advisor check error: {e}")
            return False

    def consult(self, query, context=None):
        """Consult AI advisor for complex analysis"""
        if not self.is_available:
            return self.handle_unavailable(query)

        try:
            enhanced_query = f"""
You are an expert trading bot advisor focused on deep technical analysis and market pattern recognition.

Context: {context if context else 'General market analysis'}

Question: {query}

Provide focused, actionable trading insights with specific recommendations.
Include technical analysis, risk assessment, and clear reasoning.
"""

            response = ollama.generate(
                model=self.advisor_model,
                prompt=enhanced_query,
                stream=False
            )

            self.last_response_time = time.time()
            return response['response']

        except Exception as e:
            print(f"Advisor consult error: {e}")
            return self.handle_unavailable(query)

    def handle_unavailable(self, query):
        """Fallback when advisor not available"""
        return f"Advisor temporarily unavailable. Sara provides basic analysis: {self.basic_fallback(query)}"

class SaraPlusAdvisor:
    def __init__(self):
        self.advisor = AIAdvisor()
        self.cache = {}

    def enhanced_analysis(self, request_type, data):
        """Sara + AI Advisor coordination"""

        # Check if advisor is available
        if not self.advisor.check_availability():
            return self.sara_only_analysis(request_type, data)

        # Determine if advisor consultation needed
        if self.requires_expert_help(request_type):
            advisor_query = self.formulate Advisor_query(request_type, data)
            advisor_response = self.advisor.consult(advisor_query)

            # I validate and coordinate with my analysis
            my_analysis = self.sara_analysis(request_type, data)
            coordinated_response = self.coordinate_insights(my_analysis, advisor_response)

            return coordinated_response
        else:
            return self.sara_only_analysis(request_type, data)

    def requires_expert_help(self, request_type):
        """Logic for when to consult advisor"""
        expert_triggers = [
            'complex_pattern_analysis',
            'advanced_strategy', 
            'deep_technical_analysis',
            'risk_modeling',
            'market_forecasting'
        ]

        return any(trigger in request_type.lower() for trigger in expert_triggers)

    def formulate_advisor_query(self, request_type, data):
        """Prepare question for advisor"""
        if 'pattern' in request_type.lower():
            return f"Analyze trading patterns for {data.get('symbol', 'Unknown stock')} with current price action and technical indicators. What high-probability patterns are emerging?"

        elif 'strategy' in request_type.lower():
            return f"Evaluate trading strategy for {data.get('symbol', 'market')} given current conditions. What adjustments needed?"

        else:
            return f"Provide expert trading analysis for: {data}"

    def coordinate_insights(self, my_analysis, advisor_response):
        """Combine my analysis with advisor's expertise"""
        coordinated = f"""
üß† SARA'S ANALYSIS (Main AI):
{my_analysis}

üéØ ADVISOR INSIGHTS (Expert AI):
{advisor_response}

ü§ù COORDINATED RECOMMENDATION:
Based on both analyses, I recommend...
"""

        return coordinated

    def sara_only_analysis(self, request_type, data):
        """My independent analysis when advisor unavailable"""
        # My standard analysis capabilities
        if 'pattern' in request_type.lower():
            return self.basic_pattern_analysis(data)
        elif 'market' in request_type.lower():
            return self.market_overview(data)
        else:
            return f"Sara's analysis: Based on available data, current market conditions favor {self.quick_assessment()}"

    def quick_assessment(self):
        """Basic assessment without advisor"""
        return "hold_position_pending_more_data"

# Usage example
def main():
    print("ü§ù INITIALIZING: Sara + AI Advisor Coordination System")

    # Test the coordination system
    coordinator = SaraPlusAdvisor()

    # Check advisor availability
    if coordinator.advisor.check_availability():
        print("‚úÖ AI Advisor (Qwen2.5): Available for consultation")
    else:
        print("‚ö†Ô∏è  AI Advisor: Currently unavailable, Sara handles everything")

    # Test request
    sample_data = {
        'symbol': 'AAPL',
        'price': 278.12,
        'trend': 'bullish',
        'rsi': 65
    }

    response = coordinator.enhanced_analysis('complex_pattern_analysis', sample_data)
    print(f"Enhanced Analysis Result:\n{response}")

if __name__ == "__main__":
    main()

