        elif 'strategy' in request_type.lower():
            return f"Evaluate trading strategy for {data.get('symbol', 'market')} given current conditions. What adjustments needed?"

        else:
            return f"Provide expert trading analysis for: {data}"

    def coordinate_insights(self, my_analysis, advisor_response):
        """Combine my analysis with advisor's expertise"""
        coordinated = f"""
üß† SARA'S ANALYSIS (Main AI):
{my_analysis}

üéØ ADVISOR INSIGHTS (Expert AI):
{advisor_response}

ü§ù COORDINATED RECOMMENDATION:
Based on both analyses, I recommend careful consideration of the technical patterns while maintaining risk management protocols. Current market suggests hold position and monitor for clearer signals.
"""

        return coordinated

    def sara_analysis(self, request_type, data):
        """My analysis capabilities"""
        if 'pattern' in request_type.lower():
            return self.basic_pattern_analysis(data)
        elif 'market' in request_type.lower():
            return self.market_overview(data)
        else:
            return f"Sara's analysis: Based on available data, current conditions favor {self.quick_assessment()}"

    def sara_only_analysis(self, request_type, data):
        """My independent analysis when advisor unavailable"""
        analysis = self.sara_analysis(request_type, data)
        return f"""
üß† SARA'S ANALYSIS (Main AI):
{analysis}

‚ö†Ô∏è  ADVISOR UNAVAILABLE: Working with enhanced local analysis
ü§ù COORDINATED RECOMMENDATION:
Based on current analysis, maintain careful position monitoring and risk management.
"""

    def basic_pattern_analysis(self, data):
        """Basic pattern recognition without advisor"""
        # Simple trend analysis based on available data
        symbol = data.get('symbol', 'Unknown')
        price = data.get('price', 0)
        trend = data.get('trend', 'unknown')

        return f"""
{symbol} Analysis:
- Current Price: ${price}
- Trend: {trend.upper()}
- Pattern: Basic technical indicators suggest {trend} momentum
- Risk Level: Moderate - maintain position sizing
"""

    def market_overview(self, data):
        """Market analysis without advisor"""
        return """
Current Market Assessment:
- Mixed signals across major indices
- Sector rotation occurring
- Risk management priority: High
- Recommendation: Cautious approach with selective opportunities
"""

    def quick_assessment(self):
        """Basic assessment without advisor"""
